import React, { Component } from 'react';
import axios from 'axios';

class CreatePost extends Component {
    constructor(props){
        super(props)
            this.state = {
                bikeName: "",
                bikeType: "",
                rentPrice: ""
            }
    }
    handleInputChange = (e) => {
        const { name, value} = e.target;
        this.setState({
            ...this.state,
            [name]:value,
        });
    };

    onSubmit = (e) => {
        e.preventDefault();
        const {bikeName, bikeType, rentPrice} = this.state;
        const data = {
            bikeName:bikeName,
            bikeType:bikeType,
            postrentPrice: rentPrice,
        }
        console.log(data);
axios.post ("http://localhost:5000/posts/add", data).then ((res)=>{
            if(res.data.success){
                alert("added");
                this.setState({bikeName:"", bikeType:"", rentPrice:""});
            }
        })
    }
    render() {
        return (
            <div className="col-md-10 mt-3 mx-auto">
                <h1 className="h3 mb-3 font-weight-normal">Create new car</h1>
                <form className="needs-validation" novalidate>
                    <div className="form-group">
                        <label>bikeName</label>
                        <input type="text"
                        className="form-control"
                        name="bikeName"
                        placeholder="enter bikeName"
                        value={this.state.bikeName}
                        onChange={this.handleInputChange}
                        />
                    </div>

                    <div className="form-group">
                        <label>rentPrice</label>
                        <input type="text"
                        className="form-control"
                        name="rentPrice"
                        placeholder="enter rentPrice"
                        value={this.state.rentPrice}
                        onChange={this.handleInputChange}
                        />
                    </div>

                    <div className="form-group">
                        <label>bikeType</label>
                        <input type="text"
                        className="form-control"
                        name="bikeType"
                        placeholder="enter bikeType"
                        value={this.state.bikeType}
                        onChange={this.handleInputChange}
                        />
                    </div>
<button className="btn btn-success" type="submit" onClick={this.onSubmit}>
    <i className="far fa-check-square"></i>
    &nbsp;Submit
</button>
                </form>

            </div>
        );
    }
}

export default CreatePost;
