import React, { Component } from 'react';
import axios from "axios";

class LandingPage extends Component {
  constructor(props){
    super(props);
    this.state={
      posts:[]
    }
  }
componentDidMount(){
  this.getPosts();
  this.interval = setInterval(() => {
    this.getPosts();
  }, 1000);
}

  getPosts(){
    axios.get("http://localhost:5000/posts").then( (res) => {
      if(res.data.success){
        this.setState({
          posts:res.data.posts,
        });
      }
    });
  };

  onDelete = (id) =>{
    axios.delete(`http://localhost:5000/posts/delete/${id}`).then((res)=> {
      alert("Deletede success ");
      this.getPosts();
    });
  };

  onRent = ([bikeName, bikeType, rentPrice]) =>{
    const data = {
      bikeName:bikeName,
        bikeType:bikeType,
        rentPrice:rentPrice,
    }
    axios.post("http://localhost:5000/posts/add2", data).then((res)=> {
      if (res.data.success){
        alert("added");

      }
      this.getPosts();
    });
    {
      axios.delete(`http://localhost:5000/posts/delete/${id}`).then((res)=> {
        this.getPosts();
      });
    };
  };

  componentWillUnmount() {
    clearInterval(this.interval);
  }



  render() {
    return (

      <div className="container">
        <h3>Available bicycle</h3>
        <table class="table">
  <thead>
    <tr>
      <th scope="col">$</th>
      <th scope="col">bikeName</th>
      <th scope="col">bikeType</th>
      <th scope="col">rentPrice</th>
      <th scope="col">action</th>
    </tr>
  </thead>
  <tbody>
  {this.state.posts.map((post, index ) => (
    <tr>
      <th scope="row">{index}</th>
      <td>{post.bikeName}</td>
      <td>{post.bikeType}</td>
      <td>{post.rentPrice}</td>
      <td>
        <a className="btn btn-warning" href="#" onClick={()=>this.onRent([post._id, post.bikeName, post.bikeType, post.rentPrice])}>
          <i className="fas fa-edit"></i>Edit
        </a>
        &nbsp;
        <a className="btn btn-danger" href="#" onClick={()=>this.onDelete(post._id)}>
          <i className="far fa-times-circle"></i>Delete
        </a>
      </td>
    </tr>
    ))}
  </tbody>
</table>
      </div>
    )
  }
}

export default LandingPage;
